mapper.update:
UPDATE teacher_tb
teacher_introduce=#{teacher_introduce}, teacher_curr=#{teacher_curr}, teacher_inst=#{teadcher_inst},
WHERE teacher_user_id=#{teacher_user_id.user_id} AND teacher_program=#{teacher_program.program_times}

UPDATE teacher_tb
teacher_introduce=#{teacher_introduce}, teacher_curr=#{teacher_curr}, teacher_inst=#{teadcher_inst},
WHERE teacher_user_id=#{teacher_user_id.user_id} AND teacher_program=#{teacher_program.program_times} AND teacher_selection=0;

UPDATE teacher_tb
teacher_introduce=#{teacher_introduce}, teacher_inst=#{teadcher_inst},
WHERE student_user_id=#{student_user_id.user_id} AND student_program=#{student_program.program_times} AND student_selection=0;
