mapper.update:
UPDATE user_tb

user_addr=#{user_addr},
user_email=#{user_email},
user_cp=#{user_cp}
user_name=#{user_name}

WHERE user_id=#{user_id}

mapper.select:
SELECT program_times,program_description, t.teacher_selection, 't' as "position" FROM program_tb p
INNER JOIN teacher_tb t ON p.program_times = t.teacher_program_times WHERE teacher_user_id=#{user_id}
UNION
SELECT program_times,program_description, s.student_selection, 's' as "position"  FROM program_tb p
INNER JOIN student_tb s ON p.program_times = s.student_program_times where student_user_id=#{user_id}
ORDER BY program_times desc
